<template>
  <div class="login">
    <section class="aui-flexView">
      <header class="aui-navBar aui-navBar-fixed">
        <svg class="icon" aria-hidden="true">
          <use xlink:href="#icon-arrowLeft-black"></use>
        </svg>
      </header>
      <section class="aui-scrollView">
        <div class="aui-sign-header">
          <h1>登录</h1>
        </div>
        <div class="aui-flex-from">
          <div class="aui-flex b-line">
            <div class="aui-flex-box">
              <svg class="icon" aria-hidden="true">
                <use xlink:href="#icon-phone"></use>
              </svg>
              <input v-model="phone" type="text" name="phone" placeholder="请输入手机号" />
            </div>
          </div>
          <div class="aui-flex b-line">
            <div class="aui-flex-box">
              <svg class="icon" aria-hidden="true">
                <use xlink:href="#icon-suo"></use>
              </svg>
              <input v-model="password" type="password" name="psd" placeholder="登录密码" />
            </div>
          </div>
          <div class="aui-flex-forget">
            <a href="javascript:;">忘记密码</a>
          </div>
          <div class="aui-button-sign">
            <button @click="loginEvent">登录</button>
          </div>
        </div>
      </section>
    </section>
  </div>
</template>

<script>
export default {
  name: "Login",
  data() {
    return {
      phone: '',
      password: ''
    };
  },
  components: {},
  created() {},
  mounted() {},
  methods: {
    async loginEvent() {
      let res = await this.$store.dispatch('phoneLogin',{phone:this.phone,password:this.password})
      if(res.data.code === 200){
        this.$router.push('/me')
      }
    }
  },
};
</script>
